query GetMeasurements($modelId: ID!, $includeDisabled: Boolean!) {
  model(id: $modelId) {
    id
    measurementPaths(includeDisabled: $includeDisabled) {
      ...MeasurementDetails
      __typename
    }
    __typename
  }
}

fragment MeasurementDetails on MeasurementPath {
  id
  lineType
  label
  enabled
  points {
    position {
      x
      y
      z
      __typename
    }
    floor {
      id
      meshId
      __typename
    }
    room {
      id
      meshId
      __typename
    }
    __typename
  }
  created
  modified
  __typename
}
